mapper.select:
SELECT * FROM ( SELECT A.CAMP_NO , A.CAMP_NM , A.USER_ID , A.CAMP_TY , A.STATUS , A.DEPT_NO , A.REG_DT , A.REG_ID , A.UP_ID , A.UP_DT , A.CAMP_DESC , B.USER_NM , C.USER_NM REG_NM , D.DEPT_NM , E.CD_NM CAMP_TY_NM , F.CD_NM STATUS_NM , ROW_NUMBER() OVER (ORDER BY CAMP_NO DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_CAMPAIGN A , NEO_USER B , NEO_USER C , NEO_DEPT D , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C004' AND UILANG = #{uilang}) E , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C014' AND UILANG = #{uilang}) F WHERE A.USER_ID = B.USER_ID AND A.REG_ID = C.USER_ID AND A.DEPT_NO = D.DEPT_NO AND A.CAMP_TY = E.CD AND A.STATUS = F.CD
AND A.CAMP_NM LIKE '%' || #{searchCampNm} || '%'
AND A.CAMP_TY = #{searchCampTy}
AND A.STATUS = #{searchStatus}
AND A.DEPT_NO = #{deptNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
AND A.REG_DT >= #{searchStartDt} || '000000'
AND A.REG_DT < #{searchEndDt} || '240000'
) WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

mapper.insert:
INSERT INTO NEO_CAMPAIGN
(
	  CAMP_NO
	, CAMP_NM
	, CAMP_DESC
	, USER_ID
	, CAMP_TY
	, STATUS
	, DEPT_NO
	, REG_DT
	, REG_ID
	, UP_ID
	, UP_DT 
)
VALUES
(
	  NEO_CAMPAIGN_SEQ.NEXTVAL
	, #{campNm}
	, #{campDesc}
	, #{userId}
	, #{campTy}
	, #{status}
	, #{deptNo}
	, #{regDt}
	, #{regId}
	, NULL
	, NULL 
)

mapper.update:
UPDATE NEO_CAMPAIGN
   SET CAMP_NM   = #{campNm}
	 , CAMP_DESC = #{campDesc}
	 , USER_ID   = #{userId}
	 , CAMP_TY   = #{campTy}
	 , STATUS    = #{status}
	 , DEPT_NO   = #{deptNo}
	 , UP_ID     = #{upId}
	 , UP_DT     = #{upDt}
 WHERE CAMP_NO   = #{campNo}

mapper.select:
SELECT * FROM ( SELECT NEO_TASK.TASK_NO , NEO_SUBTASK.SUB_TASK_NO , TASK_NM , NEO_TASK.SEG_NO , SEG_NM , SEND_REPEAT_CD.CD_NM SEND_REPEAT_NM , SEND_REPEAT_CD.CD SEND_REPEAT , NEO_TASK.USER_ID , SEND_DT , WORK_STATUS_CD.CD_NM WORK_STATUS_NM , WORK_STATUS_CD.CD WORK_STATUS , WORK_STATUS_CD.CD_DTL WORK_STATUS_DTL , STATUS_CD.CD STATUS , STATUS_CD.CD_NM STATUS_NM , DB_CONN_NO , SELECT_ SELECT_SQL , FROM_ FROM_SQL , WHERE_ WHERE_SQL , ORDERBY_ ORDERBY_SQL , MERGE_KEY , MERGE_COL , SEG_FL_PATH , DEPT_NM , NEO_CAMPAIGN.CAMP_NO , CAMP_NM , SUB_STATUS_CD.CD SUB_STATUS , SUB_STATUS_CD.CD_NM SUB_STATUS_NM , ROW_NUMBER() OVER (ORDER BY NEO_TASK.TASK_NO DESC, NEO_SUBTASK.SUB_TASK_NO DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_TASK , NEO_SUBTASK , NEO_SEGMENT , NEO_CAMPAIGN , NEO_CD SEND_REPEAT_CD , NEO_CD WORK_STATUS_CD , NEO_CD STATUS_CD , NEO_DEPT , NEO_CD SUB_STATUS_CD WHERE SEND_REPEAT_CD.CD_GRP = 'C017' AND SEND_REPEAT_CD.UILANG = #{uilang} AND WORK_STATUS_CD.CD_GRP = 'C034' AND WORK_STATUS_CD.UILANG = #{uilang} AND STATUS_CD.CD_GRP = 'C023' AND STATUS_CD.UILANG = #{uilang} AND SUB_STATUS_CD.CD_GRP = 'C023' AND SUB_STATUS_CD.UILANG = #{uilang} AND NEO_SUBTASK.SEND_TEST_YN = 'N' AND NEO_TASK.TASK_NO = NEO_SUBTASK.TASK_NO AND NEO_TASK.SEG_NO = NEO_SEGMENT.SEG_NO AND NEO_TASK.SEND_REPEAT = SEND_REPEAT_CD.CD AND NEO_TASK.STATUS = STATUS_CD.CD AND NEO_SUBTASK.STATUS = SUB_STATUS_CD.CD AND NEO_SUBTASK.WORK_STATUS = WORK_STATUS_CD.CD AND NEO_TASK.DEPT_NO = NEO_DEPT.DEPT_NO AND NEO_TASK.CAMP_NO = NEO_CAMPAIGN.CAMP_NO
AND NEO_TASK.DEPT_NO = #{searchDeptNo}
AND NEO_TASK.CAMP_NO = #{searchCampNo}
AND NEO_TASK.TASK_NM LIKE '%' || #{searchTaskNm} || '%'
AND NEO_TASK.DEPT_NO = #{searchDeptNo}
AND NEO_TASK.STATUS = #{searchStatus}
AND NEO_SUBTASK.WORK_STATUS NOT IN ('000', '001', '002', '003')
AND NEO_SUBTASK.WORK_STATUS IN
#{item}
AND NEO_SUBTASK.SEND_DT >= #{searchStartDt} || '000000'
AND NEO_SUBTASK.SEND_DT < #{searchEndDt} || '240000'
AND NEO_TASK.USER_ID = #{searchUserId}
) WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

mapper.update:
UPDATE NEO_TASK
   SET RECO_STATUS = #{recoStatus}
	 , EXE_USER_ID = #{exeUserId}
 WHERE TASK_NO     = #{taskNo}

UPDATE NEO_SUBTASK
   SET WORK_STATUS = #{workStatus}
 WHERE TASK_NO     = #{taskNo}
   AND SUB_TASK_NO = #{subTaskNo}

