mapper.select:
SELECT * FROM ( SELECT A.DEPT_NO , A.DEPT_NM , A.DEPT_DESC , A.STATUS , A.REG_ID , A.REG_DT , B.CD_NM STATUS_NM , ROW_NUMBER() OVER (ORDER BY A.DEPT_NO DESC) SEQ , COUNT(1) OVER() TOT_CNT FROM NEO_DEPT A, (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C009' AND USE_YN = 'Y' AND UILANG = #{uilang}) B WHERE A.STATUS = B.CD
AND A.DEPT_NM LIKE '%' || #{searchDeptNm} || '%'
AND A.STATUS = #{searchStatus}
) WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

mapper.insert:
INSERT INTO NEO_DEPT
(
	DEPT_NO
	, DEPT_NM
	, DEPT_DESC
	, STATUS
	, REG_ID
	, REG_DT
)
VALUES
(
	NEO_DEPT_SEQ.NEXTVAL
	, #{deptNm}
	, #{deptDesc}
	, #{status}
	, #{regId}
	, #{regDt}
)

mapper.update:
UPDATE NEO_DEPT
   SET DEPT_NM   = #{deptNm}
	 , DEPT_DESC = #{deptDesc}
	 , STATUS    = #{status}
	 , UP_ID     = #{upId}
	 , UP_DT     = #{upDt}
WHERE DEPT_NO    = #{deptNo}

mapper.select:
SELECT USER_ID
FROM NEO_USER
WHERE USER_ID = #{userId}

mapper.insert:
INSERT INTO NEO_USER 
(
	USER_ID
  , DEPT_NO
  , USER_NM
  , USER_PWD
  , USER_EM
  , USER_TEL
  , USER_DESC
  , STATUS
  , MAIL_FROM_NM
  , MAIL_FROM_EM
  , REPLY_TO_EM
  , RETURN_EM
  , CHARSET
  , TZ_CD
  , UILANG
  , REG_ID
  , REG_DT
)
VALUES
(
	#{userId}
  , #{deptNo}
  , #{userNm}
  , #{userPwd}
  , #{userEm}
  , #{userTel}
  , #{userDesc}
  , #{status}
  , #{mailFromNm}
  , #{mailFromEm}
  , #{replyToEm}
  , #{returnEm}
  , #{charset}
  , #{tzCd}
  , #{uilang}
  , #{regId}
  , #{regDt}
)

mapper.update:
UPDATE NEO_USER
   SET
	   DEPT_NO      = #{deptNo}
	 , USER_NM      = #{userNm}
	 
	 , USER_PWD     = #{userPwd}
	 
	 , USER_EM      = #{userEm}
	 , USER_TEL     = #{userTel}
	 , USER_DESC    = #{userDesc}
	 , STATUS       = #{status}
	 , MAIL_FROM_NM = #{mailFromNm}
	 , MAIL_FROM_EM = #{mailFromEm}
	 , RETURN_EM    = #{returnEm}
	 , REPLY_TO_EM  = #{replyToEm}
	 , CHARSET      = #{charset}
	 , TZ_CD        = #{tzCd}
	 , UILANG       = #{uilang}
	 , UP_ID        = #{upId}
	 , UP_DT        = #{upDt}
 WHERE USER_ID      = #{userId}

mapper.select:
SELECT * FROM ( SELECT A.DB_CONN_NO , A.DB_CONN_NM , A.DB_TY , A.DB_DRIVER , A.DB_URL , A.DB_CHAR_SET , A.LOGIN_ID , A.STATUS , A.REG_ID , A.REG_DT , A.UP_ID , A.UP_DT , B.CD_NM DB_TY_NM , C.CD_NM DB_CHAR_SET_NM , D.CD_NM STATUS_NM , ROW_NUMBER() OVER (ORDER BY A.DB_CONN_NO) SEQ , COUNT(1) OVER() TOT_CNT FROM NEO_DBCONN A , ( SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C033' AND UILANG = #{uilang} AND USE_YN = 'Y' ) B , ( SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C032' AND UILANG = #{uilang} AND USE_YN = 'Y' ) C , ( SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C011' AND UILANG = #{uilang} AND USE_YN = 'Y' ) D WHERE A.DB_TY = B.CD AND A.DB_CHAR_SET = C.CD AND A.STATUS = D.CD
AND A.DB_CONN_NM LIKE '%' || #{searchDbConnNm} || '%'
AND A.DB_TY = #{searchDbTy}
AND A.STATUS = #{searchStatus}
) WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

mapper.insert:
INSERT INTO NEO_DBCONN
(
	DB_CONN_NO
	, DB_CONN_NM
	, DB_TY
	, DB_DRIVER
	, DB_URL
	, DB_CHAR_SET
	, LOGIN_ID
	, LOGIN_PWD
	, DB_CONN_DESC
	, STATUS
	, REG_ID
	, REG_DT
)
VALUES
(
	NEO_DBCONN_SEQ.NEXTVAL
	, #{dbConnNm}
	, #{dbTy}
	, #{dbDriver}
	, #{dbUrl}
	, #{dbCharSet}
	, #{loginId}
	, #{loginPwd}
	, #{dbConnDesc}
	, #{status}
	, #{regId}
	, #{regDt}
)

mapper.update:
UPDATE NEO_DBCONN
  SET
	   DB_CONN_NM   = #{dbConnNm}
	 , DB_TY        = #{dbTy}
	 , DB_DRIVER    = #{dbDriver}
	 , DB_URL       = #{dbUrl}
	 , DB_CHAR_SET  = #{dbCharSet}
	 , LOGIN_ID     = #{loginId}
	 , LOGIN_PWD    = #{loginPwd}
	 , DB_CONN_DESC = #{dbConnDesc}
	 , STATUS       = #{status}
	 , UP_ID        = #{upId}
	 , UP_DT        = #{upDt}
 WHERE DB_CONN_NO   = #{dbConnNo}
