		  		)
		 WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT OPEN_CNT , BLOCK_CNT , VALID_CNT , CLICK_CNT FROM ( SELECT NVL(SUM((CASE WHEN BLOCKED_YN = 'N' THEN RESP_AR_AMT END)),0) AS OPEN_CNT , NVL(SUM((CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT END)),0) AS BLOCK_CNT FROM NEO_AR_RESPLOG WHERE
( NEO_AR_RESPLOG.TASK_NO = #{joinKey.taskNo} AND NEO_AR_RESPLOG.SUB_TASK_NO = #{joinKey.subTaskNo} )
) A, ( SELECT NVL(SUM(CLICK_AR_AMT),0) AS CLICK_CNT, NVL(SUM(VALID_AR_AMT),0) AS VALID_CNT FROM NEO_AR_LINKLOG WHERE
( NEO_AR_LINKLOG.TASK_NO = #{joinKey.taskNo} AND NEO_AR_LINKLOG.SUB_TASK_NO = #{joinKey.subTaskNo} )
) C

SELECT * FROM ( SELECT A.TASK_NO , A.TASK_NM , A.CHANNEL , A.SEND_REPEAT , A.STATUS , A.SEG_NO , A.SEND_TERM_END_DT , A.SEND_TERM_LOOP , A.REG_DT , A.CONT_FL_PATH , A.SURVEYNO , C.SEG_NM , D.USER_NM , E.CAMP_NM , F.CD_NM CHANNEL_NM , G.CD_NM SEND_REPEAT_NM , H.CD_NM STATUS_NM , ROW_NUMBER() OVER (ORDER BY A.TASK_NO DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_TASK A , NEO_SEGMENT C , NEO_USER D , NEO_CAMPAIGN E , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C002' AND UILANG = #{uilang}) F , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C017' AND UILANG = #{uilang}) G , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C023' AND UILANG = #{uilang}) H WHERE A.SEND_REPEAT = '001' AND A.SEG_NO = C.SEG_NO AND A.USER_ID = D.USER_ID AND A.CAMP_NO = E.CAMP_NO AND A.CHANNEL = F.CD AND A.SEND_REPEAT = G.CD AND A.STATUS = H.CD
AND UPPER(A.TASK_NM) LIKE '%' || #{searchTaskNm} || '%'
AND A.DEPT_NO = #{searchDeptNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
AND A.CAMP_NO = #{searchCampNo}
AND A.REG_DT >= #{searchStartDt} || '000000'
AND A.REG_DT < #{searchEndDt} ||'240000'
) WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT * FROM ( SELECT A.SUB_TASK_NO , A.SEND_DT , A.END_DT , A.WORK_STATUS , B.TASK_NO , B.TASK_NM , B.CHANNEL , B.SEND_REPEAT , B.STATUS , B.SEG_NO , B.SEND_TERM_END_DT , B.SEND_TERM_LOOP , B.REG_DT , B.CONT_FL_PATH , B.SURVEYNO , C.SEG_NM , D.USER_NM , E.CAMP_NM , F.CD_NM CHANNEL_NM , G.CD_NM SEND_REPEAT_NM , H.CD_NM STATUS_NM , ROW_NUMBER() OVER (ORDER BY A.SEND_DT DESC, A.TASK_NO DESC, A.SUB_TASK_NO DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_SUBTASK A , NEO_TASK B , NEO_SEGMENT C , NEO_USER D , NEO_CAMPAIGN E , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C002' AND UILANG = '000') F , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C017' AND UILANG = '000') G , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C034' AND UILANG = '000') H WHERE A.SEND_TEST_YN != 'Y' AND A.WORK_STATUS IN ('002','003') AND A.TASK_NO = B.TASK_NO AND B.SEG_NO = C.SEG_NO AND B.USER_ID = D.USER_ID AND B.CAMP_NO = E.CAMP_NO AND B.CHANNEL = F.CD AND B.SEND_REPEAT = G.CD AND A.WORK_STATUS = H.CD AND B.SEND_REPEAT = '001'
AND UPPER(B.TASK_NM) LIKE '%' || #{searchTaskNm} || '%'
AND B.DEPT_NO = #{searchDeptNo}
AND B.DEPT_NO = #{searchDeptNo}
AND B.USER_ID = #{searchUserId}
AND B.CAMP_NO = #{searchCampNo}
AND A.SEND_DT >= #{searchStartDt} || '000000'
AND A.SEND_DT < #{searchEndDt} ||'240000'
AND B.TASK_NO = #{taskNo}
) WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT NEO_AR_SENDLOG.TASK_NO , NEO_TASK.TASK_NM , NEO_SEGMENT.SEG_NM , NVL(SUM(SEND_AR_AMT),0) AS TOT_CNT , NVL(SUM((CASE WHEN NEO_AR_SENDLOG.SEND_RCODE = '000' THEN SEND_AR_AMT END)),0) AS SUC_CNT FROM NEO_AR_SENDLOG , NEO_TASK , NEO_SEGMENT WHERE NEO_AR_SENDLOG.TASK_NO = NEO_TASK.TASK_NO AND NEO_TASK.SEG_NO = NEO_SEGMENT.SEG_NO AND
NEO_AR_SENDLOG.TASK_NO = #{joinKey.taskNo}
GROUP BY NEO_AR_SENDLOG.TASK_NO, NEO_TASK.TASK_NM, NEO_SEGMENT.SEG_NM ORDER BY NEO_AR_SENDLOG.TASK_NO DESC

SELECT OPEN_CNT , BLOCK_CNT , VALID_CNT , CLICK_CNT FROM ( SELECT NVL(SUM((CASE WHEN BLOCKED_YN = 'N' THEN RESP_AR_AMT END)),0) AS OPEN_CNT, NVL(SUM((CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT END)),0) AS BLOCK_CNT FROM NEO_AR_RESPLOG WHERE
NEO_AR_RESPLOG.TASK_NO = #{joinKey.taskNo}
) A, ( SELECT NVL(SUM(CLICK_AR_AMT),0) AS CLICK_CNT, NVL(SUM(VALID_AR_AMT),0) AS VALID_CNT FROM NEO_AR_LINKLOG WHERE
NEO_AR_LINKLOG.TASK_NO = #{joinKey.taskNo}
) C

SELECT A.STEP1 , A.STEP2 , NVL(B.SEND_AR_AMT,0) AS CNT_STEP2 , A.CD_DESC AS NM_STEP2 FROM ( SELECT STEP1, STEP2, CD_DESC FROM NEO_RCODE WHERE KIND = '002' AND UILANG = #{uilang} ) A, ( SELECT RCODE_STEP1, RCODE_STEP2, SUM(SEND_AR_AMT) AS SEND_AR_AMT FROM NEO_AR_SENDLOG WHERE SEND_RCODE != '000' AND
NEO_AR_SENDLOG.TASK_NO = #{joinKey.taskNo}
GROUP BY RCODE_STEP1, RCODE_STEP2 ) B WHERE A.STEP1 = B.RCODE_STEP1(+) AND A.STEP2 = B.RCODE_STEP2(+) ORDER BY A.STEP1, A.STEP2

SELECT NEO_AR_SENDLOG.CAMP_NO , NEO_CAMPAIGN.CAMP_NM , NVL(SUM(SEND_AR_AMT),0) AS TOT_CNT , NVL(SUM((CASE WHEN NEO_AR_SENDLOG.SEND_RCODE = '000' THEN SEND_AR_AMT END)),0) AS SUC_CNT FROM NEO_AR_SENDLOG , NEO_CAMPAIGN WHERE NEO_AR_SENDLOG.CAMP_NO = NEO_CAMPAIGN.CAMP_NO AND
NEO_AR_SENDLOG.CAMP_NO = #{campNo}
GROUP BY NEO_AR_SENDLOG.CAMP_NO, NEO_CAMPAIGN.CAMP_NM ORDER BY NEO_AR_SENDLOG.CAMP_NO DESC

SELECT OPEN_CNT , BLOCK_CNT , VALID_CNT , CLICK_CNT FROM ( SELECT NVL(SUM((CASE WHEN BLOCKED_YN = 'N' THEN RESP_AR_AMT END)),0) AS OPEN_CNT, NVL(SUM((CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT END)),0) AS BLOCK_CNT FROM NEO_AR_RESPLOG WHERE
NEO_AR_RESPLOG.CAMP_NO = #{campNo}
) A, ( SELECT NVL(SUM(CLICK_AR_AMT),0) AS CLICK_CNT, NVL(SUM(VALID_AR_AMT),0) AS VALID_CNT FROM NEO_AR_LINKLOG WHERE
NEO_AR_LINKLOG.CAMP_NO = #{campNo}
) C

SELECT A.STEP1 , A.STEP2 , NVL(B.SEND_AR_AMT,0) AS CNT_STEP2 , A.CD_DESC AS NM_STEP2 FROM ( SELECT STEP1, STEP2, CD_DESC FROM NEO_RCODE WHERE KIND = '002' AND UILANG = #{uilang} ) A, ( SELECT RCODE_STEP1, RCODE_STEP2, SUM(SEND_AR_AMT) AS SEND_AR_AMT FROM NEO_AR_SENDLOG WHERE SEND_RCODE != '000' AND
NEO_AR_SENDLOG.CAMP_NO = #{campNo}
GROUP BY RCODE_STEP1, RCODE_STEP2 ) B WHERE A.STEP1 = B.RCODE_STEP1(+) AND A.STEP2 = B.RCODE_STEP2(+) ORDER BY A.STEP1, A.STEP2

SELECT AA.YMD , SEND_CNT , NVL(SUCC_CNT,0) SUCC_CNT , NVL(OPEN_CNT,0) OPEN_CNT , NVL(VALID_CNT,0) VALID_CNT , NVL(CLICK_CNT,0) CLICK_CNT , NVL(BLOCK_CNT,0) BLOCK_CNT FROM ( SELECT TO_CHAR((TO_DATE(A.SEND_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMM') YMD, NVL(SUM(SEND_AR_AMT),0) SEND_CNT, NVL(SUM(CASE WHEN SEND_RCODE = '000' THEN SEND_AR_AMT ELSE 0 END),0) SUCC_CNT FROM NEO_AR_SENDLOG A, NEO_USER B, NEO_TIMEZONE C WHERE A.USER_ID = B.USER_ID AND B.TZ_CD = C.TZ_CD AND A.SEND_DT >= #{searchStartDt} || '0000' AND A.SEND_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY TO_CHAR((TO_DATE(A.SEND_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMM') ) AA, ( SELECT TO_CHAR((TO_DATE(A.OPEN_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMM') YMD, NVL(SUM(RESP_AR_AMT),0) OPEN_CNT, NVL(SUM(CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT ELSE 0 END),0) BLOCK_CNT FROM NEO_AR_RESPLOG A, NEO_USER B, NEO_TIMEZONE C WHERE A.USER_ID = B.USER_ID AND B.TZ_CD = C.TZ_CD AND A.OPEN_DT >= #{searchStartDt} || '0000' AND A.OPEN_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY TO_CHAR((TO_DATE(A.OPEN_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMM') ) BB, ( SELECT TO_CHAR((TO_DATE(A.CLICK_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMM') YMD, NVL(SUM(CLICK_AR_AMT),0) CLICK_CNT, NVL(SUM(VALID_AR_AMT),0) VALID_CNT FROM NEO_AR_LINKLOG A, NEO_USER B, NEO_TIMEZONE C WHERE A.USER_ID = B.USER_ID AND B.TZ_CD = C.TZ_CD AND A.CLICK_DT >= #{searchStartDt} || '0000' AND A.CLICK_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY TO_CHAR((TO_DATE(A.CLICK_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMM') ) DD WHERE AA.YMD = BB.YMD(+) AND AA.YMD = DD.YMD(+) ORDER BY AA.YMD

SELECT EE.WEEK_NM AS WEEK_NM , NVL(SEND_CNT,0) SEND_CNT , NVL(SUCC_CNT,0) SUCC_CNT , NVL(OPEN_CNT,0) OPEN_CNT , NVL(VALID_CNT,0) VALID_CNT , NVL(CLICK_CNT,0) CLICK_CNT , NVL(BLOCK_CNT,0) BLOCK_CNT FROM ( SELECT CD, CD_NM AS WEEK_NM FROM NEO_CD WHERE CD_GRP = 'C005' AND UILANG = #{uilang} ) EE, ( SELECT DAYOFWEEK, NVL(SUM(SEND_AR_AMT),0) SEND_CNT, NVL(SUM(CASE WHEN SEND_RCODE = '000' THEN SEND_AR_AMT ELSE 0 END),0) SUCC_CNT FROM NEO_AR_SENDLOG A WHERE A.SEND_DT >= #{searchStartDt} || '0000' AND A.SEND_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY DAYOFWEEK ) AA, ( SELECT DAYOFWEEK, NVL(SUM(RESP_AR_AMT),0) OPEN_CNT, NVL(SUM(CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT ELSE 0 END),0) BLOCK_CNT FROM NEO_AR_RESPLOG A WHERE A.OPEN_DT >= #{searchStartDt} || '0000' AND A.OPEN_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY DAYOFWEEK ) BB, ( SELECT DAYOFWEEK, NVL(SUM(CLICK_AR_AMT),0) CLICK_CNT, NVL(SUM(VALID_AR_AMT),0) VALID_CNT FROM NEO_AR_LINKLOG A WHERE A.CLICK_DT >= #{searchStartDt} || '0000' AND A.CLICK_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY DAYOFWEEK ) DD WHERE EE.CD = AA.DAYOFWEEK(+) AND EE.CD = BB.DAYOFWEEK(+) AND EE.CD = DD.DAYOFWEEK(+) ORDER BY EE.CD

SELECT AA.YMD , SEND_CNT , NVL(SUCC_CNT,0) SUCC_CNT , NVL(OPEN_CNT,0) OPEN_CNT , NVL(VALID_CNT,0) VALID_CNT , NVL(CLICK_CNT,0) CLICK_CNT , NVL(BLOCK_CNT,0) BLOCK_CNT FROM ( SELECT TO_CHAR((TO_DATE(A.SEND_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMMDD') YMD, NVL(SUM(SEND_AR_AMT),0) SEND_CNT, NVL(SUM(CASE WHEN SEND_RCODE = '000' THEN SEND_AR_AMT ELSE 0 END),0) SUCC_CNT FROM NEO_AR_SENDLOG A, NEO_USER B, NEO_TIMEZONE C WHERE A.USER_ID = B.USER_ID AND B.TZ_CD = C.TZ_CD AND A.SEND_DT >= #{searchStartDt} || '0000' AND A.SEND_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY TO_CHAR((TO_DATE(A.SEND_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMMDD') ) AA, ( SELECT TO_CHAR((TO_DATE(A.OPEN_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMMDD') YMD, NVL(SUM(RESP_AR_AMT),0) OPEN_CNT, NVL(SUM(CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT ELSE 0 END),0) BLOCK_CNT FROM NEO_AR_RESPLOG A, NEO_USER B, NEO_TIMEZONE C WHERE A.USER_ID = B.USER_ID AND B.TZ_CD = C.TZ_CD AND A.OPEN_DT >= #{searchStartDt} || '0000' AND A.OPEN_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY TO_CHAR((TO_DATE(A.OPEN_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMMDD') ) BB, ( SELECT TO_CHAR((TO_DATE(A.CLICK_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMMDD') YMD, NVL(SUM(CLICK_AR_AMT),0) CLICK_CNT, NVL(SUM(VALID_AR_AMT),0) VALID_CNT FROM NEO_AR_LINKLOG A, NEO_USER B, NEO_TIMEZONE C WHERE A.USER_ID = B.USER_ID AND B.TZ_CD = C.TZ_CD AND A.CLICK_DT >= #{searchStartDt} || '0000' AND A.CLICK_DT <= #{searchEndDt} || '2400'
AND A.CAMP_NO = #{searchCampNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
GROUP BY TO_CHAR((TO_DATE(A.CLICK_DT,'YYYYMMDDHH24MISS') + (C.TZ_TERM/60)/24), 'YYYYMMDD') ) DD WHERE AA.YMD = BB.YMD(+) AND AA.YMD = DD.YMD(+) ORDER BY AA.YMD

SELECT DOMAIN_NM , SEND_CNT , SUCC_CNT , ERR_CNT FROM ( SELECT CUST_DOMAIN DOMAIN_NM , SUM(SEND_AR_AMT) SEND_CNT , SUM(CASE WHEN SEND_RCODE = '000' THEN SEND_AR_AMT ELSE 0 END) AS SUCC_CNT , SUM(CASE WHEN (SEND_RCODE != '000' AND RCODE_STEP1 = '002') THEN SEND_AR_AMT ELSE 0 END) AS ERR_CNT FROM NEO_AR_DOMAINLOG A, NEO_TASK B WHERE A.TASK_NO = B.TASK_NO AND A.SEND_DAY||'0000' >= #{searchStartDt} || '0000' AND A.SEND_DAY||'0000' < #{searchEndDt} || '2400'
AND B.CAMP_NO = #{searchCampNo}
AND B.DEPT_NO = #{searchDeptNo}
AND B.USER_ID = #{searchUserId}
GROUP BY CUST_DOMAIN ORDER BY SUM(SEND_AR_AMT) DESC ) WHERE ROWNUM <= 20

