SELECT * FROM ( SELECT A.SUB_TASK_NO , A.SEND_DT , A.END_DT , A.WORK_STATUS , B.TASK_NO , B.TASK_NM , B.CHANNEL , B.SEND_REPEAT , B.STATUS , B.SEG_NO , B.SEND_TERM_END_DT , B.SEND_TERM_LOOP , B.REG_DT , B.CONT_FL_PATH , B.SURVEYNO , C.SEG_NM , D.USER_NM , E.CAMP_NM , F.CD_NM CHANNEL_NM , G.CD_NM SEND_REPEAT_NM , H.CD_NM STATUS_NM , ROW_NUMBER() OVER (ORDER BY A.SEND_DT DESC, A.TASK_NO DESC, A.SUB_TASK_NO DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_SUBTASK A , NEO_TASK B , NEO_SEGMENT C , NEO_USER D , NEO_CAMPAIGN E , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C002' AND UILANG = '000') F , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C017' AND UILANG = '000') G , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C034' AND UILANG = '000') H WHERE A.SEND_TEST_YN != 'Y' AND A.WORK_STATUS IN ('002','003') AND A.TASK_NO = B.TASK_NO AND B.SEG_NO = C.SEG_NO AND B.USER_ID = D.USER_ID AND B.CAMP_NO = E.CAMP_NO AND B.CHANNEL = F.CD AND B.SEND_REPEAT = G.CD AND A.WORK_STATUS = H.CD
AND UPPER(B.TASK_NM) LIKE '%' || #{searchTaskNm} || '%'
AND B.DEPT_NO = #{searchDeptNo}
AND B.DEPT_NO = #{searchDeptNo}
AND B.USER_ID = #{searchUserId}
AND B.CAMP_NO = #{searchCampNo}
AND A.SEND_DT >= #{searchStartDt} || '000000'
AND A.SEND_DT < #{searchEndDt} ||'240000'
AND B.TASK_NO = #{taskNo}
) WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT SEND_CNT
	 , SUCC_CNT
	 , (SEND_CNT - SUCC_CNT) AS FAIL_CNT
	 , OPEN_CNT
	 , VALID_CNT
	 , CLICK_CNT
	 , BLOCK_CNT
FROM	(
			SELECT NVL(SUM(SEND_AR_AMT),0) SEND_CNT
				 , NVL(SUM(CASE WHEN SEND_RCODE = '000' THEN SEND_AR_AMT ELSE 0 END),0) SUCC_CNT
			  FROM NEO_AR_SENDLOG
			 WHERE TASK_NO = #{taskNo}
			   AND SUB_TASK_NO = #{subTaskNo}
		) A,
		(
			SELECT NVL(SUM(RESP_AR_AMT),0) OPEN_CNT
				 , NVL(SUM(CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT ELSE 0 END),0) BLOCK_CNT
			  FROM NEO_AR_RESPLOG
			 WHERE TASK_NO = #{taskNo}
			   AND SUB_TASK_NO = #{subTaskNo}
		) B,
		(
			SELECT NVL(SUM(CLICK_AR_AMT),0) CLICK_CNT
				 , NVL(SUM(VALID_AR_AMT),0) VALID_CNT
			  FROM NEO_AR_LINKLOG
			 WHERE TASK_NO = #{taskNo}
			   AND SUB_TASK_NO = #{subTaskNo}
		) D

SELECT *
  FROM (
		SELECT A.CUST_ID
			 , A.CUST_EM
			 , A.CUST_NM
			 , A.RETRY_CNT
			 , A.SEND_DT
			 , A.SEND_MSG
			 , ROW_NUMBER() OVER (ORDER BY A.CUST_ID, A.CUST_EM, A.CUST_NM) SEQ
			 , COUNT(1) OVER() TOTAL_COUNT
		  FROM (
			  		SELECT SUB_TASK_NO, CUST_ID, CUST_EM, CUST_NM, RETRY_CNT, SEND_DT, SEND_MSG
					  FROM NEO_SENDLOG
					 WHERE TASK_NO = #{taskNo}
					   AND SEND_RCODE != '000'
					   
					   AND SUB_TASK_NO = #{subTaskNo}
					   
					   
					   AND RCODE_STEP1 = #{step1}
					   
					   
					   AND RCODE_STEP2 = #{step2}
					   
					   
					   AND RCODE_STEP3 = #{step3}
					   
					   
					   AND CUST_DOMAIN = #{domain}
					   
				) A
				JOIN
				(
					SELECT SUB_TASK_NO, CUST_ID, CUST_EM, CUST_NM, MAX(RETRY_CNT) AS MAX_RETRY_CNT
					  FROM NEO_SENDLOG
					 WHERE TASK_NO = #{taskNo}
					   AND SEND_RCODE != '000'
					   
					   AND SUB_TASK_NO = #{subTaskNo}
					   
					   
					   AND RCODE_STEP1 = #{step1}
					   
					   
					   AND RCODE_STEP2 = #{step2}
					   
					   
					   AND RCODE_STEP3 = #{step3}
					   
					   
					   AND CUST_DOMAIN = #{domain}
					   
					 GROUP BY SUB_TASK_NO, CUST_ID, CUST_EM, CUST_NM
				) B
			 ON A.CUST_ID = B.CUST_ID
			AND A.CUST_EM = B.CUST_EM
			AND A.CUST_NM = B.CUST_NM
			AND A.RETRY_CNT = B.MAX_RETRY_CNT
		)
 WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT SUCC_CNT, OPEN_CNT, CLICK_CNT, VALID_CNT, BLOCK_CNT
  FROM (
		SELECT SUM(SEND_AR_AMT) AS SUCC_CNT FROM NEO_AR_SENDLOG
		 WHERE TASK_NO = #{taskNo}
		   AND SUB_TASK_NO = #{subTaskNo}
		   AND SEND_RCODE = '000'
		) X,
		(
		SELECT SUM(OPEN_CNT) AS OPEN_CNT, SUM(CLICK_CNT) AS CLICK_CNT, SUM(VALID_CNT) AS VALID_CNT, SUM(BLOCK_CNT) AS BLOCK_CNT
		  FROM (
				SELECT SUM(AA.RESP_AR_AMT) AS OPEN_CNT, 0 AS CLICK_CNT, 0 AS VALID_CNT
					, SUM(CASE WHEN AA.BLOCKED_YN = 'Y' THEN AA.RESP_AR_AMT ELSE 0 END) AS BLOCK_CNT
				  FROM NEO_AR_RESPLOG AA,
						(
						SELECT USER_ID, TZ_TERM FROM NEO_USER A, NEO_TIMEZONE B
						 WHERE A.TZ_CD = B.TZ_CD
						) BB
				 WHERE AA.TASK_NO = #{taskNo}
				   AND AA.SUB_TASK_NO = #{subTaskNo}
				   AND AA.USER_ID = BB.USER_ID
				 UNION ALL
				SELECT 0 AS OPEN_CNT, SUM(AA.CLICK_AR_AMT) AS CLICK_CNT, SUM(AA.VALID_AR_AMT) AS VALID_CNT, 0 AS BLOCK_CNT
				  FROM NEO_AR_LINKLOG AA,
						(
						SELECT USER_ID, TZ_TERM FROM NEO_USER A, NEO_TIMEZONE B
						 WHERE A.TZ_CD = B.TZ_CD
						) BB
				 WHERE AA.TASK_NO = #{taskNo}
				   AND AA.SUB_TASK_NO = #{taskNo}
				   AND AA.USER_ID = BB.USER_ID
				) YY
		 ) Y

		SELECT *
		  FROM (
		  
				
				SELECT A.CUST_ID
					 , A.CUST_EM
					 , A.CUST_NM
					 , A.SEND_DT
					 , A.SEND_RCODE
					 , A.SEND_MSG
					 , (
						SELECT OPEN_DT FROM NEO_RESPLOG
						 WHERE TASK_NO = #{taskNo}
						   AND SUB_TASK_NO = #{subTaskNo}
						   AND CUST_ID = A.CUST_ID
						) OPEN_DT
					 , DENIED_TYPE
		             , ROW_NUMBER() OVER (ORDER BY A.CUST_ID, A.SEND_DT) SEQ
		             , COUNT(1) OVER() TOTAL_COUNT
				  FROM NEO_SENDLOG A
				 WHERE A.TASK_NO = #{taskNo}
				   AND A.SUB_TASK_NO = #{subTaskNo}
				   AND A.SEND_RCODE = '000'
				   
				   AND A.CUST_ID = #{searchCustId}
				   
				   
				   AND A.CUST_EM = #{searchCustEm}
				   
				   
				   AND A.CUST_NM = #{searchCustNm}
				   
				

				
				SELECT A.CUST_ID
					 , A.CUST_EM
					 , A.CUST_NM
					 , A.SEND_RCODE
					 , A.SEND_DT
					 , A.SEND_MSG
					 , '' AS OPEN_DT
		             , ROW_NUMBER() OVER (ORDER BY A.CUST_ID, A.SEND_DT) SEQ
		             , COUNT(1) OVER() TOTAL_COUNT
				  FROM (
						SELECT SUB_TASK_NO, CUST_ID, CUST_EM, CUST_NM, SEND_RCODE, RETRY_CNT, SEND_DT, SEND_MSG
						  FROM NEO_SENDLOG
						 WHERE TASK_NO = #{taskNo}
						   AND SUB_TASK_NO = #{subTaskNo}
						) A,
						(
						SELECT SUB_TASK_NO, CUST_ID, CUST_EM, CUST_NM, MAX(RETRY_CNT) AS MAX_RETRY_CNT
						  FROM NEO_SENDLOG
						 WHERE TASK_NO = #{taskNo}
						   AND SUB_TASK_NO = #{subTaskNo}
						 GROUP BY SUB_TASK_NO, CUST_ID, CUST_EM, CUST_NM
						) B
				 WHERE A.SUB_TASK_NO = B.SUB_TASK_NO
				   AND A.CUST_ID = B.CUST_ID
				   AND A.CUST_EM = B.CUST_EM
				   AND A.CUST_NM = B.CUST_NM
				   AND A.RETRY_CNT = B.MAX_RETRY_CNT
				   AND SEND_RCODE != '000'
				   
				   AND A.CUST_ID = #{searchCustId}
				   
				   
				   AND A.CUST_EM = #{searchCustEm}
				   
				   
				   AND A.CUST_NM = #{searchCustNm}
				   
				

				
				SELECT A.CUST_ID
					 , A.CUST_EM
					 , A.CUST_NM
					 , A.SEND_RCODE
					 , A.SEND_DT
					 , A.SEND_MSG
					 , A.DENIED_TYPE
					 , B.OPEN_DT
		             , ROW_NUMBER() OVER (ORDER BY A.CUST_ID, A.SEND_DT) SEQ
		             , COUNT(1) OVER() TOTAL_COUNT
				  FROM NEO_SENDLOG A, NEO_RESPLOG B
				 WHERE A.TASK_NO = B.TASK_NO
				   AND A.SUB_TASK_NO = B.SUB_TASK_NO
				   AND A.CUST_ID = B.CUST_ID
				   AND A.TASK_NO = #{taskNo}
				   AND A.SUB_TASK_NO = #{subTaskNo}
				   AND A.SEND_RCODE = '000'
				   
				   AND A.CUST_ID = #{searchCustId}
				   
				   
				   AND A.CUST_EM = #{searchCustEm}
				   
				   
				   AND A.CUST_NM = #{searchCustNm}
				   
				

				
				SELECT A.CUST_ID
					 , A.CUST_EM
					 , A.CUST_NM
					 , A.SEND_DT
					 , A.DENIED_TYPE
					 , A.SEND_RCODE
					 , '' AS OPEN_DT
					 , A.SEND_MSG
		             , ROW_NUMBER() OVER (ORDER BY A.CUST_ID, A.SEND_DT) SEQ
		             , COUNT(1) OVER() TOTAL_COUNT
				  FROM NEO_SENDLOG A
				 WHERE A.TASK_NO = #{taskNo}
				   AND A.SUB_TASK_NO = #{subTaskNo}
				   AND A.SEND_RCODE = '000'
				   AND NOT EXISTS (
									SELECT '1' FROM NEO_RESPLOG B
									 WHERE B.TASK_NO = #{taskNo}
									   AND B.SUB_TASK_NO = #{subTaskNo}
									   AND B.CUST_ID = A.CUST_ID
								)
				   
				   AND A.CUST_ID = #{searchCustId}
				   
				   
				   AND A.CUST_EM = #{searchCustEm}
				   
				   
				   AND A.CUST_NM = #{searchCustNm}
				   
				


				
				SELECT A.CUST_ID
					 , A.CUST_EM
					 , A.CUST_NM
					 , A.SEND_DT
					 , A.SEND_RCODE
					 , A.SEND_MSG
					 , (
						SELECT OPEN_DT FROM NEO_RESPLOG
						 WHERE TASK_NO = #{taskNo}
						   AND SUB_TASK_NO = #{subTaskNo}
						   AND CUST_ID = A.CUST_ID
						) OPEN_DT
					 , DENIED_TYPE
		             , ROW_NUMBER() OVER (ORDER BY A.CUST_ID, A.SEND_DT) SEQ
		             , COUNT(1) OVER() TOTAL_COUNT
				  FROM NEO_SENDLOG A
				 WHERE A.TASK_NO = #{taskNo}
				   AND A.SUB_TASK_NO = #{subTaskNo}
				   AND A.SEND_RCODE = '000'
				   AND A.DENIED_TYPE ='Y'
				   
				   AND A.CUST_ID = #{searchCustId}
				   
				   
				   AND A.CUST_EM = #{searchCustEm}
				   
				   
				   AND A.CUST_NM = #{searchCustNm}
				   
				

				
				SELECT A.CUST_ID
					 , A.CUST_EM
					 , A.CUST_NM
					 , A.SEND_DT
					 , A.SEND_RCODE
					 , A.SEND_MSG
					 , (
						SELECT OPEN_DT FROM NEO_RESPLOG
						 WHERE TASK_NO = #{taskNo}
						   AND SUB_TASK_NO = #{subTaskNo}
						   AND CUST_ID = A.CUST_ID
						) OPEN_DT
					 , DENIED_TYPE
		             , ROW_NUMBER() OVER (ORDER BY A.CUST_ID, A.SEND_DT) SEQ
		             , COUNT(1) OVER() TOTAL_COUNT
				  FROM NEO_SENDLOG A
				 WHERE A.TASK_NO = #{taskNo}
				  AND A.SUB_TASK_NO =  #{subTaskNo}
				  AND A.SEND_RCODE = '000'
				  AND A.DENIED_TYPE ='N'
				   
				   AND A.CUST_ID = #{searchCustId}
				   
				   
				   AND A.CUST_EM = #{searchCustEm}
				   
				   
				   AND A.CUST_NM = #{searchCustNm}
				   
				

				
				SELECT A.CUST_ID
					 , A.CUST_EM
					 , A.CUST_NM
					 , A.SEND_DT
					 , A.SEND_RCODE
					 , A.SEND_MSG
					 , (
						SELECT OPEN_DT FROM NEO_RESPLOG
						 WHERE TASK_NO = #{taskNo}
						   AND SUB_TASK_NO = #{subTaskNo}
						   AND CUST_ID = A.CUST_ID
						) OPEN_DT
					 , DENIED_TYPE
		             , ROW_NUMBER() OVER (ORDER BY A.CUST_ID, A.SEND_DT) SEQ
		             , COUNT(1) OVER() TOTAL_COUNT
				  FROM NEO_SENDLOG A
				 WHERE A.TASK_NO = #{taskNo}
				   AND A.SUB_TASK_NO = #{subTaskNo}
				   
				   AND A.CUST_ID = #{searchCustId}
				   
				   
				   AND A.CUST_EM = #{searchCustEm}
				   
				   
				   AND A.CUST_NM = #{searchCustNm}
				   
						  
		  		)
		 WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT OPEN_CNT , BLOCK_CNT , VALID_CNT , CLICK_CNT FROM ( SELECT NVL(SUM((CASE WHEN BLOCKED_YN = 'N' THEN RESP_AR_AMT END)),0) AS OPEN_CNT , NVL(SUM((CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT END)),0) AS BLOCK_CNT FROM NEO_AR_RESPLOG WHERE
( NEO_AR_RESPLOG.TASK_NO = #{joinKey.taskNo} AND NEO_AR_RESPLOG.SUB_TASK_NO = #{joinKey.subTaskNo} )
) A, ( SELECT NVL(SUM(CLICK_AR_AMT),0) AS CLICK_CNT, NVL(SUM(VALID_AR_AMT),0) AS VALID_CNT FROM NEO_AR_LINKLOG WHERE
( NEO_AR_LINKLOG.TASK_NO = #{joinKey.taskNo} AND NEO_AR_LINKLOG.SUB_TASK_NO = #{joinKey.subTaskNo} )
) C

SELECT * FROM ( SELECT A.TASK_NO , A.TASK_NM , A.CHANNEL , A.SEND_REPEAT , A.STATUS , A.SEG_NO , A.SEND_TERM_END_DT , A.SEND_TERM_LOOP , A.REG_DT , A.CONT_FL_PATH , A.SURVEYNO , C.SEG_NM , D.USER_NM , E.CAMP_NM , F.CD_NM CHANNEL_NM , G.CD_NM SEND_REPEAT_NM , H.CD_NM STATUS_NM , ROW_NUMBER() OVER (ORDER BY A.TASK_NO DESC) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_TASK A , NEO_SEGMENT C , NEO_USER D , NEO_CAMPAIGN E , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C002' AND UILANG = #{uilang}) F , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C017' AND UILANG = #{uilang}) G , (SELECT CD, CD_NM FROM NEO_CD WHERE CD_GRP = 'C023' AND UILANG = #{uilang}) H WHERE A.SEND_REPEAT = '001' AND A.SEG_NO = C.SEG_NO AND A.USER_ID = D.USER_ID AND A.CAMP_NO = E.CAMP_NO AND A.CHANNEL = F.CD AND A.SEND_REPEAT = G.CD AND A.STATUS = H.CD
AND UPPER(A.TASK_NM) LIKE '%' || #{searchTaskNm} || '%'
AND A.DEPT_NO = #{searchDeptNo}
AND A.DEPT_NO = #{searchDeptNo}
AND A.USER_ID = #{searchUserId}
AND A.CAMP_NO = #{searchCampNo}
AND A.REG_DT >= #{searchStartDt} || '000000'
AND A.REG_DT < #{searchEndDt} ||'240000'
) WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

SELECT NEO_AR_SENDLOG.TASK_NO , NEO_TASK.TASK_NM , NEO_SEGMENT.SEG_NM , NVL(SUM(SEND_AR_AMT),0) AS TOT_CNT , NVL(SUM((CASE WHEN NEO_AR_SENDLOG.SEND_RCODE = '000' THEN SEND_AR_AMT END)),0) AS SUC_CNT FROM NEO_AR_SENDLOG , NEO_TASK , NEO_SEGMENT WHERE NEO_AR_SENDLOG.TASK_NO = NEO_TASK.TASK_NO AND NEO_TASK.SEG_NO = NEO_SEGMENT.SEG_NO AND
NEO_AR_SENDLOG.TASK_NO = #{joinKey.taskNo}
GROUP BY NEO_AR_SENDLOG.TASK_NO, NEO_TASK.TASK_NM, NEO_SEGMENT.SEG_NM ORDER BY NEO_AR_SENDLOG.TASK_NO DESC

SELECT OPEN_CNT , BLOCK_CNT , VALID_CNT , CLICK_CNT FROM ( SELECT NVL(SUM((CASE WHEN BLOCKED_YN = 'N' THEN RESP_AR_AMT END)),0) AS OPEN_CNT, NVL(SUM((CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT END)),0) AS BLOCK_CNT FROM NEO_AR_RESPLOG WHERE
NEO_AR_RESPLOG.TASK_NO = #{joinKey.taskNo}
) A, ( SELECT NVL(SUM(CLICK_AR_AMT),0) AS CLICK_CNT, NVL(SUM(VALID_AR_AMT),0) AS VALID_CNT FROM NEO_AR_LINKLOG WHERE
NEO_AR_LINKLOG.TASK_NO = #{joinKey.taskNo}
) C

SELECT A.STEP1 , A.STEP2 , NVL(B.SEND_AR_AMT,0) AS CNT_STEP2 , A.CD_DESC AS NM_STEP2 FROM ( SELECT STEP1, STEP2, CD_DESC FROM NEO_RCODE WHERE KIND = '002' AND UILANG = #{uilang} ) A, ( SELECT RCODE_STEP1, RCODE_STEP2, SUM(SEND_AR_AMT) AS SEND_AR_AMT FROM NEO_AR_SENDLOG WHERE SEND_RCODE != '000' AND
NEO_AR_SENDLOG.TASK_NO = #{joinKey.taskNo}
GROUP BY RCODE_STEP1, RCODE_STEP2 ) B WHERE A.STEP1 = B.RCODE_STEP1(+) AND A.STEP2 = B.RCODE_STEP2(+) ORDER BY A.STEP1, A.STEP2

SELECT NEO_AR_SENDLOG.CAMP_NO , NEO_CAMPAIGN.CAMP_NM , NVL(SUM(SEND_AR_AMT),0) AS TOT_CNT , NVL(SUM((CASE WHEN NEO_AR_SENDLOG.SEND_RCODE = '000' THEN SEND_AR_AMT END)),0) AS SUC_CNT FROM NEO_AR_SENDLOG , NEO_CAMPAIGN WHERE NEO_AR_SENDLOG.CAMP_NO = NEO_CAMPAIGN.CAMP_NO AND
NEO_AR_SENDLOG.CAMP_NO = #{campNo}
GROUP BY NEO_AR_SENDLOG.CAMP_NO, NEO_CAMPAIGN.CAMP_NM ORDER BY NEO_AR_SENDLOG.CAMP_NO DESC

SELECT OPEN_CNT , BLOCK_CNT , VALID_CNT , CLICK_CNT FROM ( SELECT NVL(SUM((CASE WHEN BLOCKED_YN = 'N' THEN RESP_AR_AMT END)),0) AS OPEN_CNT, NVL(SUM((CASE WHEN BLOCKED_YN = 'Y' THEN RESP_AR_AMT END)),0) AS BLOCK_CNT FROM NEO_AR_RESPLOG WHERE
NEO_AR_RESPLOG.CAMP_NO = #{campNo}
) A, ( SELECT NVL(SUM(CLICK_AR_AMT),0) AS CLICK_CNT, NVL(SUM(VALID_AR_AMT),0) AS VALID_CNT FROM NEO_AR_LINKLOG WHERE
NEO_AR_LINKLOG.CAMP_NO = #{campNo}
) C

SELECT A.STEP1 , A.STEP2 , NVL(B.SEND_AR_AMT,0) AS CNT_STEP2 , A.CD_DESC AS NM_STEP2 FROM ( SELECT STEP1, STEP2, CD_DESC FROM NEO_RCODE WHERE KIND = '002' AND UILANG = #{uilang} ) A, ( SELECT RCODE_STEP1, RCODE_STEP2, SUM(SEND_AR_AMT) AS SEND_AR_AMT FROM NEO_AR_SENDLOG WHERE SEND_RCODE != '000' AND
NEO_AR_SENDLOG.CAMP_NO = #{campNo}
GROUP BY RCODE_STEP1, RCODE_STEP2 ) B WHERE A.STEP1 = B.RCODE_STEP1(+) AND A.STEP2 = B.RCODE_STEP2(+) ORDER BY A.STEP1, A.STEP2
