mapper.select:
SELECT * FROM ( SELECT UILANG , CD_GRP , CD_GRP_NM , NVL(CD_GRP_NM_SHORT, '') AS CD_GRP_NM_SHORT , (SELECT CD_GRP FROM NEO_CD_GRP WHERE A.UP_CD_GRP = CD_GRP AND A.UILANG = UILANG) AS UP_CD_GRP , (SELECT CD_GRP_NM FROM NEO_CD_GRP WHERE A.UP_CD_GRP = CD_GRP AND A.UILANG = UILANG) AS UP_CD_GRP_NM , USE_YN , NVL(SYS_YN, 'N') AS SYS_YN , ROW_NUMBER() OVER (ORDER BY CD_GRP, CD_GRP_NM) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_CD_GRP A WHERE UILANG = #{uilang}
AND UPPER(CD_GRP) LIKE '%' || #{searchCdGrp} || '%'
AND UPPER(CD_GRP_NM) LIKE '%' || #{searchCdGrpNm} || '%'
) WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

mapper.insert:
INSERT INTO NEO_CD_GRP
(
	  UILANG
	, CD_GRP
	, CD_GRP_NM
	, CD_GRP_NM_SHORT
	, CD_GRP_DTL
	, UP_CD_GRP
	, USE_YN
	, SYS_YN
)
VALUES
(
	  #{uilang}
	, #{cdGrp}
	, #{cdGrpNm}
	, #{cdGrpNmShort}
	, #{cdGrpDtl}
	, #{upCdGrp}
	, #{useYn}
	, #{sysYn}
)

mapper.update:
UPDATE NEO_CD_GRP
   SET CD_GRP_NM = #{cdGrpNm}
	 , CD_GRP_NM_SHORT = #{cdGrpNmShort}
	 , CD_GRP_DTL = #{cdGrpDtl}
	 , UP_CD_GRP = #{upCdGrp}
	 , USE_YN = #{useYn}
 WHERE UILANG = #{uilang}
   AND CD_GRP = #{cdGrp}

mapper.delete:
DELETE FROM NEO_CD_GRP
 WHERE CD_GRP = #{cdGrp}
   AND UILANG = #{uilang}

mapper.select:
SELECT * FROM ( SELECT A.UILANG , A.CD , A.CD_NM , B.CD_GRP , B.CD_GRP_NM , NVL(CD_NM_SHORT, '') AS CD_NM_SHORT , '' AS UP_CD , '' AS UP_CD_NM --, (SELECT CD FROM NEO_CD WHERE A.UP_CD = CD AND A.UILANG = UILANG) AS UP_CD --, (SELECT CD_NM FROM NEO_CD WHERE A.UP_CD = CD AND A.UILANG = UILANG) AS UP_CD_NM , NVL(CD_DTL, '') AS CD_DTL , USE_YN , ROW_NUMBER() OVER (ORDER BY A.CD_GRP, A.CD, A.CD_NM) SEQ , COUNT(1) OVER() TOTAL_COUNT FROM NEO_CD A, NEO_CD_GRP B WHERE A.UILANG = #{uilang} AND A.CD_GRP = B.CD_GRP AND A.UILANG = B.UILANG
AND UPPER(A.CD_GRP) LIKE '%' || #{searchCdGrp} || '%'
AND UPPER(A.CD) LIKE '%' || #{searchCd} || '%'
AND UPPER(A.CD_NM) LIKE '%' || #{searchCdNm} || '%'
) WHERE SEQ BETWEEN (#{page}-1)*#{rows}+1 AND #{page}*#{rows}

mapper.insert:
INSERT INTO NEO_CD
(
	  UILANG
	, CD_GRP
	, CD
	, CD_NM
	, CD_NM_SHORT
	, CD_DTL
	, UP_CD			
	, USE_YN
)
VALUES
(
	  #{uilang}
	, #{cdGrp}
	, #{cd}
	, #{cdNm}
	, #{cdNmShort}
	, #{cdDtl}
	, #{upCd}
	, #{useYn}
)

mapper.update:
UPDATE NEO_CD
   SET CD_NM   = #{cdNm}
	 , CD_NM_SHORT = #{cdNmShort}
	 , CD_DTL  = #{cdDtl}
	 , USE_YN  = #{useYn}
 WHERE UILANG  = #{uilang}
   AND CD_GRP  = #{cdGrp}
   AND CD      = #{cd}

mapper.delete:
DELETE FROM NEO_CD
 WHERE CD_GRP = #{cdGrp}
   AND CD     = #{cd}
